REPLACE INTO `idioms` (`id`, `unit_id`, `idiom`, `meaning_id`, `example_sentence`, `sentence_translation`, `example_conversation`) VALUES
	(1, 1, 'Once in a blue moon', 'Sesuatu yang terjadi sangat jarang.', 'I only go to the cinema once in a blue moon.', 'Saya hanya pergi ke bioskop sangat jarang.', 'A: "Do you see your brother often?"\nB: "No, he lives abroad. I see him once in a blue moon."'),
	(2, 1, 'Green with envy', 'Merasa sangat cemburu atau iri.', 'She was green with envy when she saw my new car.', 'Dia sangat iri saat melihat mobil baruku.', 'A: "Did you see Sarah\'s vacation photos?"\nB: "Yes, I\'m green with envy! It looked amazing."'),
	(3, 2, 'A piece of cake', 'Sesuatu yang sangat mudah dilakukan.', 'The exam was a piece of cake.', 'Ujiannya sangat mudah.', 'A: "Was the setup difficult?"\nB: "Not at all, it was a piece of cake."'),
	(4, 4, 'Break a leg', 'Semoga berhasil (digunakan untuk mendoakan keberhasilan).', 'You have a presentation today, right? Break a leg!', 'Kamu ada presentasi hari ini, kan? Semoga berhasil!', 'A: "I\'m so nervous about my audition."\nB: "Don\'t worry, you\'ll be great. Break a leg!"'),
	(5, 11, 'blood is thicker than water', 'hubungan keluarga lebih penting', 'Anna gave her son the job. You know, blood is thicker than water.', 'Anna memberikan pekerjaan itu kepada putranya. Tahu sendiri kan, hubungan keluarga lebih penting.', 'A: "Kenapa kamu meminjamkannya begitu banyak uang?"\nB: "Dia adikku. Hubungan keluarga lebih penting."'),
	(6, 11, 'a bitter pill to swallow', 'kenyataan pahit', 'Losing his job after ten years was a bitter pill to swallow.', 'Kehilangan pekerjaan setelah sepuluh tahun adalah kenyataan pahit yang harus diterima.', 'A: "Dia baru tahu partner bisnisnya menipunya."\nB: "Itu pasti kenyataan pahit yang sulit diterima."'),
	(7, 11, 'to cough up', 'membayar (dengan enggan)', 'Cough up the rent or leave the apartment.', 'Bayar uang sewa atau tinggalkan apartemen ini.', 'A: "Apakah kamu sudah dapat uang yang dia pinjam?"\nB: "Sudah, akhirnya dia membayarnya setelah kutagih tiga kali."'),
	(8, 11, 'to feel it in one\'s bones', 'punya firasat', 'I can feel it in my bones that our team will win.', 'Saya punya firasat tim kita akan menang.', 'A: "Kamu yakin ini jalan yang benar?"\nB: "Entahlah, tapi firasatku mengatakan kita sudah dekat."'),
	(9, 11, 'a fly in the ointment', 'ganjalan / masalah kecil', 'We want to buy a house, but the fly in the ointment is we don\'t have enough money.', 'Kami ingin membeli rumah, tapi masalah kecilnya adalah kami tidak punya cukup uang.', 'A: "Pestanya sempurna!"\nB: "Hampir. Satu-satunya ganjalan adalah musiknya yang jelek."'),
	(10, 11, 'to get burned', 'dirugikan / tertipu', 'If you invest too much, you may get burned.', 'Jika kamu berinvestasi terlalu banyak, kamu bisa dirugikan.', 'A: "Aku kehilangan semua uangku di skema itu."\nB: "Sudah kubilang hati-hati. Kamu tertipu kan."'),
	(11, 11, 'to give someone a taste of their own medicine', 'membalas perlakuan buruk', 'If Lena keeps shouting, someone will give her a taste of her own medicine.', 'Jika Lena terus berteriak, seseorang akan membalas perlakuan buruknya.', 'A: "Dia selalu mengabaikan teleponku."\nB: "Lain kali dia menelepon, abaikan saja. Balas perlakuannya."'),
	(12, 11, 'to have a lot of nerve', 'berani / kurang ajar', 'Elmer has a lot of nerve asking for another raise.', 'Elmer berani sekali meminta kenaikan gaji lagi.', 'A: "Dia mengambil tempat parkirku tepat di depanku!"\nB: "Wah, kurang ajar sekali orang itu."'),
	(13, 11, 'to hold one\'s breath', 'menahan napas / menunggu cemas', 'Hold your breath and count to ten to calm down.', 'Tahan napasmu dan hitung sampai sepuluh untuk menenangkan diri.', 'A: "Hasilnya akan diumumkan lima menit lagi."\nB: "Aku tahu, aku menunggu dengan cemas!"'),
	(14, 11, 'sick and tired', 'muak / jenuh', 'I\'m sick and tired of doing all the housework myself.', 'Saya muak mengerjakan semua pekerjaan rumah sendirian.', 'A: "Kamu mau ikut rapat?"\nB: "Tidak, aku sudah muak dengan semua alasannya."'),
	(15, 11, 'to take pains', 'berusaha keras', 'If you take pains to do something well, it will be successful.', 'Jika kamu berusaha keras untuk melakukan sesuatu dengan baik, itu akan berhasil.', 'A: "Laporan ini detail sekali."\nB: "Terima kasih, aku berusaha keras agar laporan ini akurat."'),
	(16, 2, 'the apple of one\'s eye', 'kesayangan / anak emas', 'The baby is the apple of her grandfather\'s eye. He thinks that she\'s wonderful.', 'Bayi itu adalah kesayangan kakeknya. Dia pikir cucunya itu luar biasa.', 'A: "Kamu sepertinya sangat menyayangi putri bungsunya."\nB: "Tentu saja, dia adalah anak emasku."'),
	(17, 2, 'baloney', 'omong kosong', 'His speech about helping the poor is baloney. He wouldn\'t even give his best friend a dime.', 'Pidatonya tentang menolong orang miskin itu omong kosong. Dia bahkan tidak mau memberi temannya sepeser pun.', 'A: "Dia bilang akan menyelesaikan proyek ini dalam sehari."\nB: "Itu omong kosong belaka."'),
	(18, 2, 'to cream someone', 'mengalahkan telak', 'Our basketball team really creamed its opponent. Our team won by a score of 120 to 60.', 'Tim basket kami benar-benar mengalahkan lawannya telak. Tim kami menang dengan skor 120 banding 60.', 'A: "Bagaimana pertandingan semalam?"\nB: "Kami mengalahkan mereka telak! Skor akhirnya 5-0."'),
	(19, 2, 'fishy', 'mencurigakan', 'We found the front door open and suspected something fishy was going on.', 'Kami menemukan pintu depan terbuka dan curiga ada sesuatu yang tidak beres sedang terjadi.', 'A: "Dia menawariku ponsel baru dengan harga sangat murah."\nB: "Terdengar mencurigakan. Hati-hati saja."'),
	(20, 2, 'to go bananas', 'sangat marah / sangat senang', 'She went bananas when she heard she had won first prize in the talent contest.', 'Dia sangat senang ketika mendengar dia memenangkan hadiah pertama dalam kontes bakat.', 'A: "Apa yang terjadi saat kamu bilang ke ibumu kalau kamu merusak vas kesayangannya?"\nB: "Dia marah besar!"'),
	(21, 2, 'in a pickle', 'dalam masalah', 'Bill was in a pickle. After filling his car with gas, he could not find the money to pay.', 'Bill sedang dalam masalah. Setelah mengisi bensin mobilnya, dia tidak bisa menemukan uang untuk membayar.', 'A: "Dompetku ketinggalan dan taksinya sudah menunggu."\nB: "Sepertinya kamu dalam masalah. Sini aku pinjami uang."'),
	(22, 2, 'a lemon', 'barang rusak / jelek', 'The new tape player I bought was a lemon.', 'Pemutar kaset baru yang saya beli ternyata barang rusak.', 'A: "Ponsel baruku sering mati sendiri."\nB: "Sebaiknya kamu kembalikan. Sepertinya kamu dapat barang yang jelek."'),
	(23, 2, 'nuts', 'gila / tidak waras', 'Tony must be nuts to pay over a hundred dollars for a shirt.', 'Tony pasti gila membayar lebih dari seratus dolar untuk sebuah kemeja.', 'A: "Dia mau mendaki gunung itu sendirian."\nB: "Apa dia gila? Itu sangat berbahaya!"'),
	(24, 2, 'a peach', 'orang yang baik hati', 'Susan visited me and bought groceries when I was sick. She\'s a peach.', 'Susan menjengukku dan membelikan bahan makanan saat aku sakit. Dia orang yang sangat baik.', 'A: "Tetanggaku membantuku memperbaiki mobil tanpa dibayar."\nB: "Wah, dia baik sekali."'),
	(25, 2, 'peanuts', 'uang receh / sangat murah', 'Rose buys used clothes and gets nice-looking clothes for peanuts.', 'Rose membeli pakaian bekas dan mendapatkan pakaian yang bagus dengan harga sangat murah.', 'A: "Berapa harga barang itu?"\nB: "Hampir gratis. Aku dapat dengan harga sangat murah di bazar."'),
	(26, 2, 'a piece of cake', 'sangat mudah', 'The math test was a piece of cake for Erik.', 'Tes matematika itu sangat mudah bagi Erik.', 'A: "Apakah ujiannya sulit?"\nB: "Tidak, sangat mudah."'),
	(27, 3, 'at first sight', 'sekilas', 'At first sight, she seems okay, but I may change my mind later.', 'Sekilas, dia kelihatannya baik-baik saja, tapi mungkin nanti pendapatku berubah.', 'A: "Apa pendapatmu tentang desain baru ini?"\nB: "Sekilas kelihatannya bagus, tapi aku perlu memeriksanya lebih teliti."'),
	(28, 3, 'forty winks', 'tidur sebentar / tidur ayam', 'I sometimes take forty winks on Saturday afternoon.', 'Saya kadang-kadang tidur sebentar pada hari Sabtu sore.', 'A: "Kamu terlihat lelah."\nB: "Iya, sepertinya aku akan tidur ayam sebentar sebelum pesta nanti malam."'),
	(29, 3, 'in seventh heaven', 'sangat bahagia', 'Maxwell was in seventh heaven when he learned he had gotten the job.', 'Maxwell sangat bahagia ketika dia tahu dia mendapatkan pekerjaan itu.', 'A: "Bagaimana perasaannya setelah menang lotre?"\nB: "Dia sangat bahagia selama berminggu-minggu!"'),
	(30, 3, 'of two minds', 'bimbang / ragu', 'Betty was of two minds about her future: teacher or actress?', 'Betty bimbang tentang masa depannya: menjadi guru atau aktris?', 'A: "Apakah kamu jadi ikut liburan bersama kami?"\nB: "Aku masih ragu. Kelihatannya seru, tapi biayanya juga sangat mahal."'),
	(31, 3, 'on cloud nine', 'sangat senang', 'Juanita was on cloud nine when she graduated at the top of her class.', 'Juanita sangat senang ketika dia lulus sebagai yang terbaik di kelasnya.', 'A: "Apakah dia suka kejutan ulang tahunnya?"\nB: "Dia sangat senang sepanjang hari!"'),
	(32, 3, 'on second thought', 'setelah dipikir ulang', 'On second thought, I decided to keep my old TV for a while.', 'Setelah dipikir ulang, saya memutuskan untuk menyimpan TV lama saya untuk sementara waktu.', 'A: "Ayo kita makan malam di luar."\nB: "Sebenarnya, setelah kupikir-pikir, aku agak lelah. Pesan antar saja, ya."'),
	(33, 3, 'to put two and two together', 'menyimpulkan sesuatu', 'We saw our neighbors packing, we put two and two together and concluded they were leaving.', 'Kami melihat tetangga kami berkemas, kami pun menyimpulkan bahwa mereka akan pindah.', 'A: "Bagaimana kamu tahu dia merencanakan pesta kejutan?"\nB: "Dia bersikap aneh dan aku mendengarnya berbisik di telepon. Aku hanya menyimpulkannya saja."'),
	(34, 3, 'second nature', 'sudah terbiasa / alami', 'Learning to golf is second nature to some people.', 'Belajar bermain golf terasa alami bagi sebagian orang.', 'A: "Kamu mengetik dengan sangat cepat!"\nB: "Aku sudah melakukannya selama 20 tahun. Sudah seperti kebiasaan bagiku sekarang."'),
	(35, 3, 'second to none', 'terbaik', 'The food in that restaurant is second to none.', 'Makanan di restoran itu adalah yang terbaik.', 'A: "Apakah dia penyanyi yang bagus?"\nB: "Bagus? Dia yang terbaik!"'),
	(36, 3, 'six of one, half a dozen of the other', 'sama saja', 'We can either stay home or go to the movie. It\'s six of one, half a dozen of the other.', 'Kita bisa tinggal di rumah atau pergi ke bioskop. Sama saja.', 'A: "Kita naik bis atau kereta? Harganya sama."\nB: "Sama saja."'),
	(37, 3, 'sixth sense', 'firasat / intuisi', 'Lisa has a sixth sense for directions.', 'Lisa punya intuisi yang kuat untuk arah.', 'A: "Bagaimana kamu tahu aku akan meneleponmu?"\nB: "Entahlah, hanya firasat saja."'),
	(49, 4, 'a big mouth', 'mulut besar / suka membocorkan', 'My brother has such a big mouth. He told everything I said to our mother.', 'Adikku suka sekali membocorkan rahasia. Dia menceritakan semua yang kukatakan kepada ibu kami.', 'A: "Jangan beritahu Sarah tentang kejutannya."\nB: "Aku tahu, dia kan mulutnya besar."'),
	(50, 4, 'by heart', 'hafal luar kepala', 'I know all my friends\' telephone numbers by heart.', 'Saya hafal semua nomor telepon teman-teman saya di luar kepala.', 'A: "Apakah kamu butuh teks pidatonya?"\nB: "Tidak, terima kasih. Aku sudah hafal di luar kepala."'),
	(51, 4, 'to cost an arm and a leg', 'sangat mahal', 'Everything in that fancy store costs an arm and a leg.', 'Semua barang di toko mewah itu harganya sangat mahal.', 'A: "Aku suka tas barumu!"\nB: "Terima kasih, tapi harganya sangat mahal."'),
	(52, 4, 'to have a sweet tooth', 'suka makanan manis', 'I know you have a sweet tooth, so I bought you a box of chocolates.', 'Aku tahu kamu suka makanan manis, jadi aku membelikanmu sekotak cokelat.', 'A: "Kamu mau hidangan penutup?"\nB: "Tentu! Kamu tahu kan aku suka yang manis-manis."'),
	(53, 4, 'head over heels in love', 'jatuh cinta berat', 'Pam and Tony are head over heels in love.', 'Pam dan Tony sedang jatuh cinta berat.', 'A: "Lihat mereka, tidak bisa berhenti saling pandang."\nB: "Iya, mereka benar-benar sedang jatuh cinta berat."'),
	(54, 4, 'a long face', 'wajah murung / sedih', 'Judy didn\'t get an invitation to the party, so she walked around with a long face.', 'Judy tidak mendapat undangan ke pesta, jadi dia berjalan-jalan dengan wajah murung.', 'A: "Kenapa wajahmu murung begitu?"\nB: "Aku gagal ujian."'),
	(55, 4, 'nosey', 'suka mencampuri urusan', 'My nosey neighbors are always watching me.', 'Tetanggaku yang suka ingin tahu itu selalu mengawasiku.', 'A: "Kenapa dia menanyakan banyak pertanyaan pribadi?"\nB: "Abaikan saja, dia memang orang yang suka ingin tahu."'),
	(56, 4, 'a pain in the neck', 'menjengkelkan', 'Waiting for the bus on the cold, windy corner is a pain in the neck.', 'Menunggu bus di sudut yang dingin dan berangin itu sangat menjengkelkan.', 'A: "Bisakah kamu membantuku dengan masalah komputer ini?"\nB: "Tentu. Berurusan dengan komputer lemot memang menyebalkan."'),
	(57, 4, 'to pull someone\'s leg', 'mengerjai / bercanda', 'Tomorrow is not a holiday. I don\'t believe Rich. He\'s just pulling my leg.', 'Besok bukan hari libur. Aku tidak percaya Rich. Dia hanya mengerjaiku.', 'A: "Kamu benar-benar menang lotre?"\nB: "Tentu saja tidak! Aku cuma bercanda."'),
	(58, 4, 'to see eye to eye', 'sepakat / sependapat', 'Lori and Nick never argue. They see eye to eye on almost everything.', 'Lori dan Nick tidak pernah bertengkar. Mereka sependapat tentang hampir segalanya.', 'A: "Apakah kamu akur dengan bos barumu?"\nB: "Iya, kami sepakat dalam banyak hal."'),
	(59, 4, 'to shake a leg', 'cepat / buru-buru', 'Shake a leg! We have to be at school in twenty minutes.', 'Cepat! Kita harus sudah di sekolah dua puluh menit lagi.', 'A: "Filmnya mulai 10 menit lagi!"\nB: "Ayo cepat atau kita akan terlambat!"'),
	(60, 5, 'to go Dutch', 'bayar masing-masing', 'When I go out to eat with my friends, we usually go Dutch.', 'Saat saya pergi makan dengan teman-teman, kami biasanya bayar masing-masing.', 'A: "How much do I owe you for dinner?"\nB: "Nothing, let\'s just go Dutch."'),
	(61, 5, 'a jack of all trades', 'serba bisa', 'Louise can fix TVs, paint houses, and build shelves.', 'Louise bisa memperbaiki TV, mengecat rumah, dan membuat rak.', 'A: "Do you know anyone who can fix my computer and also repair my bike?"\nB: "You should ask my dad. He\'s a jack of all trades."'),
	(62, 5, 'to keep up with the Joneses', 'ikut-ikutan gaya hidup', 'Kate and Ken just bought the same car as their neighbors.', 'Kate dan Ken baru saja membeli mobil yang sama dengan tetangga mereka.', 'A: "Why did they buy such an expensive phone?"\nB: "They\'re just trying to keep up with the Joneses."'),
	(63, 5, 'a man/girl Friday', 'asisten serba bisa', 'Griselda got a job as a girl Friday at the local TV studio.', 'Griselda mendapat pekerjaan sebagai asisten serba bisa di studio TV lokal.', 'A: "I\'m so busy. I need someone to answer calls and schedule my meetings."\nB: "It sounds like you need a girl Friday."'),
	(64, 5, 'a man/woman of means', 'orang kaya', 'My cousin has a business that makes a large profit every year.', 'Sepupu saya punya bisnis yang menghasilkan keuntungan besar setiap tahun.', 'A: "He just donated a lot of money to the charity."\nB: "Well, he is a man of means."'),
	(65, 5, 'the real McCoy', 'barang asli / bukan tiruan', 'The large diamond in her ring isn\'t an imitation. It\'s the real McCoy.', 'Berlian besar di cincinnya bukanlah tiruan. Itu barang asli.', 'A: "Is that a designer handbag?"\nB: "Yes, I bought it from the official store. It\'s the real McCoy."'),
	(66, 5, 'to run in the family', 'menurun dalam keluarga', 'Omar and his sister are both very thin. It must run in the family.', 'Omar dan saudara perempuannya sama-sama sangat kurus. Itu pasti turunan dalam keluarga.', 'A: "You and your brother are both great at music."\nB: "I guess it runs in the family. Our mother was a pianist."'),
	(67, 5, 'a smart aleck', 'sok tahu / sok pintar', 'Alexandra is such a smart aleck. She always thinks she\'s right.', 'Alexandra itu sok pintar. Dia selalu berpikir dialah yang benar.', 'A: "Actually, the correct way to do that is..."\nB: "Don\'t be such a smart aleck, I know what I\'m doing."'),
	(68, 5, 'any Tom, Dick, or Harry', 'orang sembarangan', 'The boss didn\'t just hire any Tom, Dick, or Harry.', 'Bos tidak mempekerjakan orang sembarangan.', 'A: "This is a very important project."\nB: "I know, we can\'t let just any Tom, Dick, or Harry work on it."'),
	(69, 5, 'a wise guy', 'sok pintar / suka menyela', 'Don\'t be such a wise guy and act as if you know it all.', 'Jangan jadi sok pintar dan bertingkah seolah kamu tahu segalanya.', 'A: "Are you sure that\'s the right answer?"\nB: "Listen, wise guy, I was the one who read the manual."'),
	(70, 6, 'as blind as a bat', 'buta seperti kelelawar', 'I\'m as blind as a bat without my glasses.', 'Saya tidak bisa melihat dengan jelas tanpa kacamata.', 'A: "Can you read that sign over there?"\nB: "Not a chance. I\'m as blind as a bat without my glasses."'),
	(71, 6, 'as quiet as a mouse', 'diam seperti tikus', 'The students were as quiet as a mouse.', 'Para siswa sangat diam.', 'A: "I didn\'t hear you come in."\nB: "I know, I was as quiet as a mouse because I didn\'t want to wake the baby."'),
	(72, 6, 'a bookworm', 'kutu buku', 'Howard reads twelve books a week. He\'s a real bookworm.', 'Howard membaca dua belas buku seminggu. Dia benar-benar seorang kutu buku.', 'A: "What are you getting your sister for her birthday?"\nB: "Probably a gift card to the bookstore. She\'s a huge bookworm."'),
	(73, 6, 'a copycat', 'peniru', 'My little brother is a copycat.', 'Adik laki-lakiku seorang peniru.', 'A: "Hey, I was going to wear that outfit today!"\nB: "Stop being such a copycat!"'),
	(74, 6, 'chicken', 'penakut', 'Richard is too chicken to try waterskiing.', 'Richard terlalu penakut untuk mencoba ski air.', 'A: "Are you going on the rollercoaster?"\nB: "No way, I\'m too much of a chicken."'),
	(75, 6, 'an early bird', 'orang yang bangun pagi', 'Allan arrives at the office before everyone else.', 'Allan tiba di kantor sebelum orang lain.', 'A: "The tour starts at 6 AM."\nB: "That\'s no problem for me. I\'m an early bird."'),
	(76, 6, 'to eat like a horse', 'makan sangat banyak', 'Jess eats like a horse after hiking.', 'Jess makan sangat banyak setelah mendaki.', 'A: "Wow, you finished all that food!"\nB: "I was so hungry! I always eat like a horse after I exercise."'),
	(77, 6, 'pigheaded', 'keras kepala', 'Adam is so pigheaded that he never listens to anyone.', 'Adam sangat keras kepala sehingga dia tidak pernah mendengarkan siapa pun.', 'A: "I tried to tell him it was a bad idea."\nB: "I know, he can be really pigheaded sometimes."'),
	(78, 6, 'to smell a rat', 'mencurigai sesuatu', 'I could smell a rat when someone offered me a gold watch for $5.', 'Saya mulai curiga ketika seseorang menawari saya jam tangan emas seharga $5.', 'A: "He said the project was finished, but he won\'t show it to us."\nB: "Hmm, I smell a rat."'),
	(79, 6, 'to talk turkey', 'bicara langsung / jujur', 'Let\'s stop being polite and talk turkey.', 'Mari kita berhenti bersikap sopan dan bicara langsung ke intinya.', 'A: "We\'ve discussed the general idea. Now, we need to discuss the price."\nB: "Okay, let\'s talk turkey. What is your budget?"'),
	(80, 6, 'to work like a dog', 'kerja keras sekali', 'Javier works two jobs and goes to college. He works like a dog.', 'Javier punya dua pekerjaan dan juga kuliah. Dia bekerja sangat keras.', 'A: "You look exhausted."\nB: "I am. I had to work like a dog to finish this report on time."'),
	(81, 7, 'dirt cheap', 'sangat murah', 'She sold a lot of her things dirt cheap.', 'Dia menjual banyak barangnya dengan harga sangat murah.', 'A: "How much did you pay for this jacket?"\nB: "I got it on sale. It was dirt cheap!"'),
	(82, 7, 'down-to-earth', 'sederhana / membumi', 'Oleg gives valuable advice. He\'s down-to-earth.', 'Oleg memberikan nasihat yang berharga. Dia orang yang bijaksana.', 'A: "I met the CEO yesterday."\nB: "How was she?"\nA: "Surprisingly down-to-earth and very friendly."'),
	(83, 7, 'to go downhill', 'menurun / memburuk', 'The company is going downhill rapidly.', 'Perusahaan itu memburuk dengan cepat.', 'A: "How\'s the new season of that TV show?"\nB: "It\'s terrible. The quality has really gone downhill."'),
	(84, 7, 'to make a mountain out of a molehill', 'membesar-besarkan masalah', 'Sally is very worried about one bad test. She\'s making a mountain out of a molehill.', 'Sally sangat khawatir tentang satu nilai ujian yang jelek. Dia membesar-besarkan masalah.', 'A: "I got one small stain on my new shirt! It\'s ruined!"\nB: "Calm down. Don\'t make a mountain out of a molehill."'),
	(85, 7, 'once in a blue moon', 'sangat jarang', 'Katie sees her brother only once in a blue moon.', 'Katie bertemu saudara laki-lakinya sangat jarang.', 'A: "Do you ever go to the opera?"\nB: "Only once in a blue moon. It\'s too expensive."'),
	(86, 7, 'out of the woods', 'keluar dari masalah', 'He was very ill, but now he\'s out of the woods.', 'Dia tadinya sakit parah, tapi sekarang dia sudah keluar dari masa kritis.', 'A: "How is the company doing financially?"\nB: "We had a tough quarter, but I think we are out of the woods now."'),
	(87, 7, 'out of this world', 'luar biasa / hebat', 'The ice cream at that store is out of this world.', 'Es krim di toko itu luar biasa enaknya.', 'A: "Have you tried the cake she baked?"\nB: "Yes! It was out of this world."'),
	(88, 7, 'over the hill', 'terlalu tua', 'I used to run five miles. Now I struggle with two. I must be over the hill.', 'Dulu saya biasa lari lima mil. Sekarang, lari dua mil saja susah. Saya pasti sudah terlalu tua.', 'A: "My dad wants to start a rock band."\nB: "Isn\'t he a bit over the hill for that?"'),
	(89, 9, 'the tip of the iceberg', 'puncak masalah / bagian kecil', 'Jonathan seems unhappy, but that\'s only the tip of the iceberg.', 'Jonathan kelihatannya tidak bahagia, tapi itu hanyalah bagian kecil dari masalahnya.', 'A: "We found a few bugs in the software."\nB: "I\'m afraid that\'s just the tip of the iceberg. There are many more problems."'),
	(90, 7, 'up the creek', 'dalam masalah besar', 'My car is in the shop. Without it, I\'m up the creek.', 'Mobilku ada di bengkel. Tanpa mobil itu, aku dalam masalah besar.', 'A: "I forgot my wallet at home and the bill is due."\nB: "Well, we are really up the creek now."'),
	(91, 7, 'to win by a landslide', 'menang telak', 'Dan won by a landslide with 88 out of 100 votes.', 'Dan menang telak dengan 88 dari 100 suara.', 'A: "How did the election go?"\nB: "The incumbent candidate won by a landslide."'),
	(92, 8, 'to get a kick out of something', 'menikmati sesuatu', 'I really get a kick out of Lisa\'s jokes. She always makes me laugh.', 'Saya sangat menikmati lelucon Lisa. Dia selalu membuatku tertawa.', 'A: "Why do you watch those old comedies?"\nB: "I don\'t know, I just get a kick out of them."'),
	(93, 8, 'to go fly a kite', 'pergi sana! / jangan ganggu', 'Stop bothering me. Why don\'t you just go fly a kite?', 'Berhenti menggangguku. Kenapa kamu tidak pergi saja?', 'A: "Can I borrow some money again?"\nB: "I\'ve already helped you enough. Go fly a kite!"'),
	(94, 8, 'a good sport', 'orang yang sportif', 'I beat Albert five times, but he\'s such a good sport.', 'Saya mengalahkan Albert lima kali, tapi dia sangat sportif.', 'A: "He didn\'t get upset even though his team lost badly."\nB: "Yeah, he\'s always a good sport."'),
	(95, 8, 'in the same boat', 'dalam situasi yang sama', 'Now that we\'re both out of work, we\'re in the same boat.', 'Sekarang karena kita berdua kehilangan pekerjaan, kita berada dalam situasi yang sama.', 'A: "I have a huge exam to study for."\nB: "Me too. Looks like we\'re in the same boat this weekend."'),
	(96, 8, 'to keep the ball rolling', 'menjaga agar tetap berjalan', 'Let\'s keep the ball rolling. We still need to collect more money.', 'Mari kita jaga agar ini tetap berjalan. Kita masih perlu mengumpulkan lebih banyak uang.', 'A: "We\'ve finished the first part of the project."\nB: "Great! Let\'s keep the ball rolling and start the next phase."'),
	(97, 8, 'no dice', 'tidak bisa / tidak setuju', 'Tom wanted the party at my place, but I said no dice.', 'Tom ingin pestanya di tempatku, tapi aku bilang tidak bisa.', 'A: "Did you ask your boss for a raise?"\nB: "I did, but no dice. He said the company can\'t afford it right now."'),
	(98, 8, 'off base', 'salah / tidak tepat', 'You\'re off base with that answer. Try again.', 'Jawabanmu tidak tepat. Coba lagi.', 'A: "I think the problem is with the engine."\nB: "I checked it already. Your guess is completely off base."'),
	(99, 8, 'on the ball', 'cekatan / sigap', 'Sophie is an excellent manager. She\'s really on the ball.', 'Sophie adalah manajer yang hebat. Dia sangat sigap.', 'A: "Did you remind John about the meeting?"\nB: "No need. He\'s always on the ball."'),
	(100, 8, 'to put one\'s cards on the table', 'bicara terus terang', 'Let me put my cards on the table. Either we sell more or we\'ll close the business.', 'Biar saya bicara terus terang. Antara kita menjual lebih banyak atau kita akan menutup bisnis ini.', 'A: "We need to make a decision."\nB: "Okay, I\'ll put my cards on the table. I don\'t think this plan will work."'),
	(101, 8, 'right off the bat', 'langsung / seketika', 'Una got the correct answer right off the bat.', 'Una langsung mendapatkan jawaban yang benar.', 'A: "How do you like your new job?"\nB: "I loved it right off the bat. Everyone was so friendly."'),
	(102, 8, 'sink or swim', 'berhasil atau gagal', 'We\'ll do our best to start our restaurant, sink or swim.', 'Kita akan melakukan yang terbaik untuk memulai restoran kita, berhasil atau gagal.', 'A: "They gave the new employee a difficult project on his first day."\nB: "Wow, it\'s a sink or swim situation for him."'),
	(103, 9, 'back and forth', 'bolak-balik', 'Elvis rode the same bus route for years. He went back and forth from San Antonio to Austin.', 'Elvis menaiki rute bus yang sama selama bertahun-tahun. Dia bolak-balik dari San Antonio ke Austin.', 'A: "Have you decided on a date for the meeting?"\nB: "Not yet, we\'ve been emailing back and forth all day."'),
	(104, 9, 'by and large', 'secara umum / umumnya', 'By and large, Americans eat a light breakfast.', 'Secara umum, orang Amerika sarapan ringan.', 'A: "How was your vacation?"\nB: "It rained a few days, but by and large, we had a wonderful time."'),
	(105, 9, 'a cock-and-bull story', 'cerita bohong / mengada-ada', 'He told me a cock-and-bull story about thinking our date was on Sunday.', 'Dia memberiku cerita bohong tentang mengira kencan kita hari Minggu.', 'A: "Why was he late?"\nB: "He gave me some cock-and-bull story about his car being chased by aliens."'),
	(106, 9, 'fair and square', 'jujur dan adil', 'I won the game fair and square.', 'Saya memenangkan permainan itu dengan jujur dan adil.', 'A: "Are you sure you counted the votes correctly?"\nB: "Yes, there\'s no doubt. She won the election fair and square."'),
	(107, 9, 'ins and outs', 'seluk-beluk', 'If you know the ins and outs of computers, you should be able to get a good job.', 'Jika kamu tahu seluk-beluk komputer, kamu seharusnya bisa mendapatkan pekerjaan yang bagus.', 'A: "Can you teach me how to use this software?"\nB: "Sure. I\'ve been using it for years, so I know all the ins and outs."'),
	(108, 9, 'odds and ends', 'barang-barang kecil', 'There were only odds and ends left at the sale.', 'Hanya tersisa barang-barang kecil di obral itu.', 'A: "What\'s in that drawer?"\nB: "Just some odds and ends - pens, paper clips, things like that."'),
	(109, 9, 'right and left', 'dari segala arah', 'The orders were coming in right and left.', 'Pesanan datang dari segala arah.', 'A: "Was the party a success?"\nB: "Absolutely! People were giving compliments right and left."'),
	(110, 9, 'safe and sound', 'selamat dan sehat', 'Everyone was safe and sound after the accident.', 'Semua orang selamat dan sehat setelah kecelakaan itu.', 'A: "Did you find your lost cat?"\nB: "Yes, he came back this morning, safe and sound!"'),
	(111, 9, 'a song and dance', 'alasan panjang lebar', 'I wish we didn\'t have to listen to such a long song and dance.', 'Andai saja kita tidak harus mendengarkan alasan sepanjang itu.', 'A: "Why didn\'t you finish the report?"\nB: "He gave me a whole song and dance about his computer crashing."'),
	(112, 9, 'spick-and-span', 'bersih sekali', 'Angelina always cleans the house so that everything is spick-and-span.', 'Angelina selalu membersihkan rumah sehingga semuanya bersih sekali.', 'A: "Wow, your kitchen looks amazing!"\nB: "Thanks, I spent all morning cleaning. It\'s spick-and-span now."'),
	(113, 9, 'wear and tear', 'kerusakan karena pemakaian', 'The carpet near the door gets a lot of wear and tear.', 'Karpet di dekat pintu menjadi cepat rusak karena sering dipakai.', 'A: "My phone is getting slow."\nB: "That\'s just normal wear and tear after a few years."'),
	(114, 10, 'as sharp as a tack', 'cerdas / tajam pikirannya', 'Tommy is only three, but he’s as sharp as a tack.', 'Tommy baru berusia tiga tahun, tapi dia sangat cerdas.', 'A: "Wow, your little brother already knows the alphabet!"\nB: "Yeah, he’s as sharp as a tack."'),
	(115, 10, 'to bury the hatchet', 'berdamai / mengakhiri konflik', 'Let’s forget our disagreement and bury the hatchet.', 'Mari kita lupakan perselisihan kita dan berdamai.', 'A: "I heard you argued with Kate last week."\nB: "We talked yesterday and decided to bury the hatchet."'),
	(116, 10, 'a flash in the pan', 'keberhasilan sesaat', 'I got a 100 on the final test, but that was just a flash in the pan.', 'Aku dapat nilai 100 di ujian akhir, tapi itu hanya keberhasilan sesaat.', 'A: "You played one great game and then lost the next three!"\nB: "I know, it was just a flash in the pan."'),
	(117, 10, 'to have a screw loose', 'agak gila / tidak waras', 'Anatoly didn’t take a jacket even though it was cold. He must have a screw loose.', 'Anatoly tidak membawa jaket padahal dingin. Pasti ada yang salah di kepalanya.', 'A: "It’s snowing and he’s wearing flip-flops!"\nB: "He definitely has a screw loose."'),
	(118, 10, 'on pins and needles', 'gelisah / cemas', 'I was on pins and needles when the teacher was giving back the tests.', 'Aku sangat cemas saat guru mengembalikan hasil ulangan.', 'A: "How did you feel before the results were announced?"\nB: "I was on pins and needles the whole time."'),
	(119, 10, 'to pan out', 'berhasil / berjalan baik', 'If things pan out, we hope to start a computer service business.', 'Kalau semuanya berjalan baik, kami berencana membuka usaha servis komputer.', 'A: "Are you still planning to open a café?"\nB: "We’ll see if it pans out financially."'),
	(120, 10, 'potluck', 'makan bersama (bawa sendiri)', 'Our party was a potluck dinner.', 'Pesta kami berupa makan malam potluck.', 'A: "What should I bring to the gathering?"\nB: "It’s a potluck, so just bring your favorite dish."'),
	(121, 10, 'soap opera', 'sinetron', 'My aunt has watched the same soap opera for ten years.', 'Bibi saya sudah menonton sinetron yang sama selama sepuluh tahun.', 'A: "Why is she crying?"\nB: "Her favorite soap opera character just died."'),
	(122, 10, 'to sponge off', 'numpang hidup / bergantung', 'Gary has sponged off us long enough.', 'Gary sudah cukup lama hidup bergantung pada kami.', 'A: "Is Luke still living at his parents’ house?"\nB: "Yeah, he keeps sponging off them."'),
	(123, 10, 'to throw in the towel', 'menyerah', 'She threw in the towel and quit the race.', 'Ia menyerah dan keluar dari perlombaan.', 'A: "I can’t solve this math problem!"\nB: "Don’t throw in the towel yet, try once more."'),
	(124, 10, 'a wet blanket', 'perusak suasana / tidak antusias', 'Andy is such a wet blanket. He never wants to go to parties.', 'Andy benar-benar perusak suasana. Dia tak pernah mau pergi ke pesta.', 'A: "Come on, dance with us!"\nB: "Stop being a wet blanket and join the fun."'),
	(125, 1, 'the black market', 'pasar gelap', 'You can go to the bank to change dollars. But if you change money on the black market, you often get more money for each dollar.', 'Kamu bisa ke bank untuk tukar dolar. Tapi kalau tukar di pasar gelap, kamu biasanya dapat lebih banyak uang per dolar.', 'A: "Where did you get such a good exchange rate?"\nB: "I traded on the black market, but be careful—it’s risky."'),
	(126, 1, 'to feel blue', 'merasa sedih', 'I was alone on my birthday and feeling blue. Then Anny called and invited me out, and I felt better.', 'Aku sendirian di ulang tahunku dan merasa sedih. Lalu Anny menelpon dan mengajak keluar, jadi aku merasa lebih baik.', 'A: "You look down today."\nB: "Yeah, I’m feeling a bit blue. Maybe a movie will cheer me up."'),
	(127, 1, 'the green light', 'lampu hijau / izin', 'The bank has given us the money. Now we have the green light to start the project.', 'Bank sudah memberi kami dana. Sekarang kami punya lampu hijau untuk mulai proyek.', 'A: "When can we launch the new app?"\nB: "We just got the green light from management—next Monday!"'),
	(128, 1, 'in black and white', 'hitam di atas putih', 'I couldn’t believe it, but there it was in black and white, as clear as it could be.', 'Aku tak percaya, tapi itu tertulis hitam di atas putih, sejelas-jelasnya.', 'A: "Are you sure the fee is waived?"\nB: "Yep, it’s right here in black and white."'),
	(129, 1, 'in the black', 'punya uang / laba', 'Theo earns a thousand dollars a week. He’s always in the black.', 'Theo menghasilkan seribu dolar seminggu. Dia selalu punya uang.', 'A: "How’s your new business doing?"\nB: "Great! We’re finally in the black this quarter."'),
	(130, 1, 'in the red', 'tekor / rugi', 'I never have enough money to pay my bills. I’m always in the red.', 'Aku tak pernah punya cukup uang untuk bayar tagihan. Aku selalu tekor.', 'A: "Why can’t we order pizza tonight?"\nB: "My account is in the red until payday."'),
	(131, 1, 'out of the blue', 'tiba-tiba', 'The news of the factory’s closing came out of the blue. No one was expecting it.', 'Berita tentang penutupan pabrik datang tiba-tiba. Tak ada yang menyangka.', 'A: "I got a scholarship offer from London!"\nB: "Wow, that came out of the blue. Congrats!"'),
	(132, 1, 'the red carpet', 'karpet merah', 'When the movie star visited, our town rolled out the red carpet.', 'Ketika bintang film itu berkunjung, kotaku menyiapkan sambutan karpet merah.', 'A: "Did you see the mayor’s speech for the guest?"\nB: "Yeah, they really rolled out the red carpet."'),
	(133, 1, 'red tape', 'birokrasi rumit', 'Every time you want to get a new passport, you have to go through a lot of red tape.', 'Setiap kali ingin paspor baru, kamu harus melalui banyak birokrasi rumit.', 'A: "Why is your visa delayed?"\nB: "So much red tape—forms, stamps, interviews…"'),
	(134, 1, 'a white lie', 'kebohongan kecil', 'I didn’t feel like going out. So I told a white lie, and I said I didn’t feel well.', 'Aku tak ingin pergi. Jadi aku bilang bohong kecil: aku bilang aku kurang enak badan.', 'A: "You said you were sick, but I saw you at the mall!"\nB: "Okay, it was just a white lie—I needed some me-time."'),
	(135, 11, 'blood is thicker than water', 'hubungan keluarga lebih penting', 'Anna gave her son the job. You know, blood is thicker than water.', 'Anna memberikan pekerjaan itu kepada putranya. Hubungan keluarga memang lebih kuat.', 'A: "Why did the boss hire his cousin?"\nB: "Blood is thicker than water, I guess."'),
	(136, 11, 'a bitter pill to swallow', 'kenyataan pahit', 'Losing his job after ten years was a bitter pill to swallow.', 'Kehilangan pekerjaan setelah sepuluh tahun adalah kenyataan yang pahit untuk diterima.', 'A: "How did he take the news?"\nB: "It was a bitter pill to swallow, but he’s coping."'),
	(137, 11, 'to cough up', 'membayar (dengan enggan)', 'Cough up the rent or leave the apartment.', 'Bayar sewa atau kosongkan apartemen.', 'A: "The landlord wants the money today."\nB: "I’ll have to cough up the rent by five or we’re out."'),
	(138, 11, 'to feel it in one’s bones', 'punya firasat', 'I can feel it in my bones that our team will win.', 'Aku punya firasat tim kami akan menang.', 'A: "Are you sure it will snow?"\nB: "Yeah, I can feel it in my bones."'),
	(139, 11, 'a fly in the ointment', 'ganjalan / masalah kecil', 'We want to buy a house, but the fly in the ointment is we don’t have enough money.', 'Kami ingin beli rumah, tapi ganjalannya kami tidak punya cukup uang.', 'A: "So the trip is almost planned!"\nB: "Yep, the only fly in the ointment is the visa delay."'),
	(140, 11, 'to get burned', 'dirugikan / tertipu', 'If you invest too much, you may get burned.', 'Kalau kamu investasi terlalu besar, kamu bisa dirugikan.', 'A: "He put all his savings into that crypto coin."\nB: "He really got burned when it crashed."'),
	(141, 11, 'to give someone a taste of their own medicine', 'membalas perlakuan buruk', 'If Lena keeps shouting, someone will give her a taste of her own medicine.', 'Kalau Lena terus berteriak, seseorang akan membalas perlakuannya.', 'A: "He never replies to emails on time."\nB: "Let’s give him a taste of his own medicine and delay ours too."'),
	(142, 11, 'to have a lot of nerve', 'berani / kurang ajar', 'Elmer has a lot of nerve asking for another raise.', 'Elmer sangat berani meminta kenaikan gaji lagi.', 'A: "She cut the line right in front of me!"\nB: "That girl has a lot of nerve."'),
	(143, 11, 'to hold one’s breath', 'menahan napas / menunggu cemas', 'Hold your breath and count to ten to calm down.', 'Tahan napasmu dan hitung sampai sepuluh untuk menenangkan diri.', 'A: "When will the test results come out?"\nB: "I’m holding my breath until tomorrow."'),
	(144, 11, 'sick and tired', 'muak / jenuh', 'I’m sick and tired of doing all the housework myself.', 'Aku sudah muak mengerjakan semua pekerjaan rumah sendirian.', 'A: "You look annoyed."\nB: "I’m sick and tired of his constant excuses."'),
	(145, 11, 'to take pains', 'berusaha keras', 'If you take pains to do something well, it will be successful.', 'Kalau kamu berusaha keras mengerjakannya dengan baik, itu akan berhasil.', 'A: "Your presentation was flawless!"\nB: "Thanks, I really took pains to prepare it."'),
	(146, 12, 'to bark up the wrong tree', 'salah sasaran / tuduh yang salah', 'If you think I’m responsible, you’re barking up the wrong tree.', 'Kalau kamu pikir aku yang bertanggung-jawab, kamu salah sasaran.', 'A: "You broke the printer!"\nB: "Hey, you’re barking up the wrong tree—it was Rina."'),
	(147, 12, 'to beat around the bush', 'bertele-tele / tidak langsung', 'Please don’t beat around the bush. Just answer yes or no.', 'Tolong jangan bertele-tele. Jawab ya atau tidak saja.', 'A: "Will you come to the meeting?"\nB: "Stop beating around the bush and give me a straight answer."'),
	(148, 12, 'a bed of roses', 'hidup yang nyaman', 'My job is no bed of roses. I get a good salary, but I work long hours.', 'Pekerjaanku bukanlah hidup yang nyaman. Gaji bagus, tapi aku kerja lembur terus.', 'A: "You must love your high-paying job!"\nB: "It’s no bed of roses—90-hour weeks!"'),
	(149, 12, 'bushed', 'sangat lelah', 'I’ve been working nonstop for ten hours. I’m really bushed.', 'Aku sudah kerja terus selama sepuluh jam. Aku benar-benar kecapekan.', 'A: "Want to play basketball tonight?"\nB: "Can’t, I’m totally bushed."'),
	(150, 12, 'to hit the hay', 'tidur', 'I’m very tired and it’s late. So I’m going to hit the hay.', 'Aku sangat lelah dan sudah malam. Jadi aku akan tidur.', 'A: "It’s only nine and you’re in pajamas?"\nB: "Yep, time to hit the hay—big day tomorrow."'),
	(151, 12, 'in a nutshell', 'secara singkat', 'In a nutshell, Sally’s getting married and moving to New York.', 'Singkatnya, Sally akan menikah dan pindah ke New York.', 'A: "Give me the update in a nutshell."\nB: "Project delayed two weeks, budget unchanged."'),
	(152, 12, 'the last straw', 'pemicu terakhir / batas kesabaran', 'Today I lost my wallet. That’s the last straw.', 'Hari ini aku kehilangan dompet. Itu lah pemicu terakhirku.', 'A: "Why did you quit suddenly?"\nB: "They cut my pay again—that was the last straw."'),
	(153, 12, 'to nip something in the bud', 'mencegah sejak awal', 'I’d better nip the habit in the bud and start eating less.', 'Sebaiknya aku cegah kebiasaan ini sejak awal dan mulai makan lebih sedikit.', 'A: "You’re snacking every night!"\nB: "I know, I need to nip it in the bud before I gain weight."'),
	(154, 12, 'through the grapevine', 'dari gosip / kabar burung', 'I heard through the grapevine that you’re moving to New York.', 'Aku dengar dari gosip bahwa kamu akan pindah ke New York.', 'A: "Who told you I got promoted?"\nB: "Just heard it through the grapevine."'),
	(155, 12, 'to turn over a new leaf', 'memulai hidup baru', 'James decided to turn over a new leaf.', 'James memutuskan untuk memulai hidup baru.', 'A: "He stopped smoking and started jogging!"\nB: "Yeah, he’s really turned over a new leaf."'),
	(156, 12, 'up a tree', 'dalam kesulitan / bingung', 'I’m up a tree with this homework assignment.', 'Aku bingung dengan tugas rumah ini.', 'A: "Can you help me with this math problem? I’m up a tree."\nB: "Sure, let’s solve it together."'),
	(157, 13, 'to be in someone’s shoes', 'berada di posisi orang lain', 'I wouldn’t like to be in Leroy’s shoes when Dad sees the broken window.', 'Aku tak mau berada di posisi Leroy saat Ayah melihat jendela yang pecah.', 'A: "I’m so nervous about tomorrow’s exam."\nB: "Put yourself in my shoes—I have two exams!"'),
	(158, 13, 'dressed to kill', 'berpakaian sangat menarik', 'Blodwyn went to the party dressed to kill in his new black suit.', 'Blodwyn pergi ke pesta berpakaian sangat menarik dengan setelan hitam barunya.', 'A: "Look at Maria—those heels!"\nB: "She’s definitely dressed to kill tonight."'),
	(159, 13, 'a feather in one’s cap', 'prestasi yang membanggakan', 'Passing the driver’s test on the first try was a real feather in my cap.', 'Lulus ujian mengemudi di percobaan pertama adalah prestasi yang membanggakan.', 'A: "You got the scholarship? Congrats!"\nB: "Thanks—definitely a feather in my cap."'),
	(160, 13, 'to handle someone with kid gloves', 'memperlakukan dengan hati-hati', 'The president gets angry easily. You have to handle her with kid gloves.', 'Presidennya mudah marah. Kamu harus memperlakukannya dengan hati-hati.', 'A: "Any tips for dealing with the boss?"\nB: "Handle her with kid gloves—she hates blunt e-mails."'),
	(161, 13, 'hot under the collar', 'marah', 'He got hot under the collar because the plane was late.', 'Dia marah karena pesawatnya terlambat.', 'A: "Why is Dad yelling?"\nB: "He’s hot under the collar about the traffic ticket."'),
	(162, 13, 'to keep one’s shirt on', 'tetap tenang', 'Just keep your shirt on, and you’ll get your turn.', 'Tetap tenang, giliranmu akan datang juga.', 'A: "I want my coffee now!"\nB: "Keep your shirt on—it’s brewing."'),
	(163, 13, 'to keep something under one’s hat', 'merahasiakan sesuatu', 'I’m getting married, but keep it under your hat.', 'Aku akan menikah, tapi jangan disebar ya.', 'A: "Who told you about the merger?"\nB: "Can’t say—keep it under your hat."'),
	(164, 13, 'to lose one’s shirt', 'kehilangan banyak uang', 'Bob invested a lot and lost his shirt.', 'Bob investasi besar dan kehilangan banyak uang.', 'A: "He sold his car and his house."\nB: "Yeah, he really lost his shirt on that start-up."'),
	(165, 13, 'on a shoestring', 'dengan dana terbatas', 'Alice started a business on a shoestring.', 'Alice memulai bisnis dengan dana sangat terbatas.', 'A: "How did you travel across Europe?"\nB: "On a shoestring—hostels and overnight buses."'),
	(166, 13, 'a stuffed shirt', 'orang yang terlalu formal', 'Higgins is such a stuffed shirt.', 'Higgins benar-benar orang yang terlalu formal dan kaku.', 'A: "He wore a tux to a casual lunch!"\nB: "What a stuffed shirt."'),
	(167, 13, 'tied to someone’s apron strings', 'tergantung pada orang lain', 'Basil never does anything unless his mother says it’s okay.', 'Basil tidak pernah berbuat apa-apa kecuali jika ibunya mengizinkan.', 'A: "He’s 30 and still asks Mom before dating!"\nB: "Totally tied to her apron strings."'),
	(168, 14, 'the big time', 'sukses besar / terkenal', 'Mafalda used to work in a supermarket. Then she hit the big time as a movie star.', 'Mafalda dulu kerja di supermarket. Lalu dia sukses besar menjadi bintang film.', 'A: "Did you see Leo’s new mansion?"\nB: "Yeah, he finally hit the big time."'),
	(169, 14, 'to call it a day', 'menyudahi pekerjaan', 'When I finish writing this, I’m going to call it a day and go to bed.', 'Kalau sudah selesai menulis ini, aku akan berhenti dan tidur.', 'A: "It’s 8 p.m. and the report is done."\nB: "Let’s call it a day and head home."'),
	(170, 14, 'for the time being', 'untuk sementara waktu', 'We have no extra money, so for the time being we can’t move.', 'Kami tidak punya uang ekstra, jadi untuk sementara kami belum bisa pindah.', 'A: "When will the office reopen?"\nB: "For the time being we’ll keep working remotely."'),
	(171, 14, 'high time', 'sudah waktunya', 'It’s high time we bought a new car.', 'Sudah waktunya kami beli mobil baru.', 'A: "This computer is ten years old!"\nB: "It’s high time you replaced it."'),
	(172, 14, 'in no time', 'sangat cepat', 'The paramedics were here in no time.', 'Tim medis tiba dengan sangat cepat.', 'A: "How long did the repair take?"\nB: "He finished it in no time at all."'),
	(173, 14, 'in the nick of time', 'tepat waktu / nyaris terlambat', 'She grabbed the vase in the nick of time before it fell.', 'Dia menangkap vas itu tepat pada waktunya sebelum jatuh.', 'A: "Did you catch the train?"\nB: "Yep, jumped on in the nick of time."'),
	(174, 14, 'to kill time', 'membunuh waktu / isi waktu', 'We were early for the movie, so we killed time at a coffee shop.', 'Kami datang lebih awal, jadi kami isi waktu di kopi shop.', 'A: "Our flight’s delayed two hours."\nB: "Let’s kill time at the bookstore."'),
	(175, 14, 'to make time', 'meluangkan waktu', 'I’ll make time in the morning to meet with you.', 'Aku akan meluangkan waktu pagi ini untuk bertemu denganmu.', 'A: "Can we discuss the proposal?"\nB: "Sure, I’ll make time after lunch."'),
	(176, 14, 'on the spur of the moment', 'secara spontan / mendadak', 'They decided to go to the mountains on the spur of the moment.', 'Mereka memutuskan pergi ke pegunungan secara spontan.', 'A: "Why are you in Paris?"\nB: "It was on the spur of the moment—we bought tickets last night!"'),
	(177, 14, 'to take one’s time', 'tidak terburu-buru', 'It’s better to take your time than to hurry and make mistakes.', 'Lebih baik tidak terburu-buru daripada cepat-cepat lalu salah.', 'A: "You’re still decorating the cake?"\nB: "I’m taking my time to make it perfect."'),
	(178, 14, 'the time is right', 'saat yang tepat', 'The time is right to invest in a house.', 'Saat ini adalah waktu yang tepat untuk investasi rumah.', 'A: "Should I ask her to marry me?"\nB: "Yes, the time is right."'),
	(179, 15, 'to break the ice', 'mencairkan suasana', 'Cedric is shy. At parties, he finds it hard to break the ice.', 'Cedric pemalu. Di pesta, dia sulit mencairkan suasana.', 'A: "Everyone\'s quiet at this meeting."\nB: "I\'ll tell a joke to break the ice."'),
	(180, 15, 'a breeze', 'mudah sekali', 'Because I studied English every day, the exam was a breeze.', 'Karena aku belajar bahasa Inggris tiap hari, ujiannya mudah sekali.', 'A: "Was the driving test hard?"\nB: "Nope, it was a breeze."'),
	(181, 15, 'come rain or shine', 'apapun yang terjadi', 'Uncle Jeremiah will be at my birthday party come rain or shine.', 'Paman Jeremiah akan hadir di pesta ulang tahunku apapun yang terjadi.', 'A: "The forecast says it might storm."\nB: "We\'ll grill outside come rain or shine."'),
	(182, 15, 'a fair-weather friend', 'teman saat senang saja', 'Judy refused to help with my report. She\'s just a fair-weather friend.', 'Judy menolak membantu laporanku. Dia teman senang-senang saja.', 'A: "Where were your buddies when you were broke?"\nB: "Gone—fair-weather friends, all of them."'),
	(183, 15, 'full of hot air', 'omong kosong', 'Many politicians are full of hot air.', 'Banyak politisi hanya omong kosong.', 'A: "He promised free flights for everyone!"\nB: "Sounds full of hot air to me."'),
	(184, 15, 'to have one\'s head in the clouds', 'tidak realistis / melamun', 'Celia doesn\'t realize she\'s in danger of failing. She has her head in the clouds.', 'Celia tak sadar dia berisiko gagal. Dia melamun terus.', 'A: "He thinks he\'ll be a millionaire next month."\nB: "Head in the clouds, as usual."'),
	(185, 15, 'to rain cats and dogs', 'hujan deras sekali', 'It must be raining cats and dogs—the rain\'s pounding on the roof.', 'Pasti hujan deras sekali—airnya mengetuk atap.', 'A: "Bring an umbrella!"\nB: "It\'s raining cats and dogs out there."'),
	(186, 15, 'to save something for a rainy day', 'menabung untuk masa sulit', 'Hank saves $40 every week for a rainy day.', 'Hank menabung 40 dolar tiap minggu untuk masa sulit.', 'A: "Why don\'t you spend your bonus?"\nB: "I\'m saving it for a rainy day."'),
	(187, 15, 'snowed under', 'kewalahan / terlalu sibuk', 'I have to stay late at the office—I\'m snowed under with work.', 'Aku harus lembur—aku kewalahan dengan pekerjaan.', 'A: "Coffee break?"\nB: "Can\'t, I\'m snowed under with reports."'),
	(188, 15, 'under the weather', 'merasa tidak enak badan', 'Berta was feeling under the weather, so she didn\'t go to work.', 'Berta merasa tidak enak badan, jadi dia tak masuk kerja.', 'A: "You skipping the gym?"\nB: "Yeah, feeling a bit under the weather."'),
	(189, 15, 'to weather the storm', 'bertahan dalam masa sulit', 'Our business has had problems, but we\'ll be okay if we can weather the storm.', 'Bisnis kami bermasalah, tapi kami akan baik-baik saja jika bisa bertahan dalam badai.', 'A: "Sales are down 30%."\nB: "We\'ll cut costs and weather the storm."'),
	(190, 16, 'to bring down the house', 'membuat penonton sangat terhibur', 'The comedian’s jokes brought down the house.', 'Lelucon sang komedian membuat penonton tertawa dan bertepuk tangan semua.', 'A: "How was the stand-up show?"\nB: "Hilarious—every joke brought down the house."'),
	(191, 16, 'down the drain', 'sia-sia / terbuang', 'Spending more money on that old car is just money down the drain.', 'Menghabiskan lebih banyak uang untuk mobil tua itu cuma buang-buang uang.', 'A: "I bought new tires for the junker."\nB: "That’s cash down the drain—sell it already."'),
	(192, 16, 'to drive someone up the wall', 'membuat kesal / gila', 'Loud music late at night will drive your neighbors up the wall.', 'Musik keras larut malam akan membuat tetangga kesal setengah mati.', 'A: "The baby’s crying again."\nB: "It drives me up the wall when I’m trying to study."'),
	(193, 16, 'to get one’s foot in the door', 'mulai masuk ke dunia kerja', 'Take any job in that company to get your foot in the door.', 'Ambil saja pekerjaan apa pun di perusahaan itu untuk memulai.', 'A: "They’re hiring interns!"\nB: "Perfect chance to get my foot in the door."'),
	(194, 16, 'to hit home', 'menyadarkan / terasa sekali', 'The problems hit home when I lost my job.', 'Masalah itu baru terasa sekali setelah aku kehilangan pekerjaan.', 'A: "Be careful on the road."\nB: "It really hit home after my cousin’s accident."'),
	(195, 16, 'to hit the ceiling', 'marah besar', 'Humphrey hit the ceiling when he realized his car had been stolen.', 'Humphrey marah besar saat tahu mobilnya dicuri.', 'A: "Mom saw my bad report card."\nB: "She’ll hit the ceiling for sure."'),
	(196, 16, 'on the fence', 'ragu-ragu / belum memutuskan', 'I’m on the fence about continuing school or getting a job.', 'Aku masih ragu mau lanjut sekolah atau kerja.', 'A: "Which phone are you buying?"\nB: "Still on the fence between iPhone and Samsung."'),
	(197, 16, 'on the house', 'gratis', 'Customers get dessert on the house if they order a meal.', 'Pemesan makanan dapat pencuci mulut gratis.', 'A: "The waiter said our drinks are on the house!"\nB: "Must be because it’s my birthday."'),
	(198, 16, 'on the shelf', 'tidak berguna / tidak aktif lagi', 'Some people feel on the shelf after they retire.', 'Beberapa orang merasa tidak berguna setelah pensiun.', 'A: "Dad misses the office."\nB: "He hates feeling on the shelf—let’s find him a hobby."'),
	(199, 16, 'to take steps', 'mengambil tindakan', 'We have to take steps to repair the roof before it rains.', 'Kita harus mulai bertindak memperbaiki atap sebelum hujan.', 'A: "The mold is spreading."\nB: "Time to take steps and call a contractor."'),
	(200, 16, 'under the table', 'secara diam-diam / ilegal', 'It’s illegal to take money under the table to vote for someone.', 'Melakukan sesuatu secara diam-diam (sering ilegal).', 'A: "He paid the workers under the table."\nB: "Risky—tax office could find out."'),
	(201, 17, 'as busy as a bee', 'sibuk sekali', 'Maxine is always working. She’s as busy as a bee.', 'Maxine selalu bekerja. Dia sibuk sekali.', 'A: "Can Maxine join lunch?"\nB: "Not today—she’s as busy as a bee finishing the report."'),
	(202, 17, 'as fit as a fiddle', 'sangat sehat', 'Bart trains every day. He’s as fit as a fiddle.', 'Bart latihan setiap hari. Dia sangat sehat.', 'A: "Does Grandpa still jog?"\nB: "Yep, 5 km daily—fit as a fiddle at eighty!"'),
	(203, 17, 'as good as gold', 'sangat baik / penurut', 'My nephew Elrod was no trouble. He was as good as gold.', 'Keponakanku Elrod tidak merepotkan sama sekali. Dia sangat penurut.', 'A: "Were the kids noisy on the flight?"\nB: "Nope, they were as good as gold."'),
	(204, 17, 'as happy as a lark', 'sangat bahagia', 'Marcus was as happy as a lark when he got engaged.', 'Marcus sangat bahagia saat lamaran diterima.', 'A: "Why is she humming today?"\nB: "She’s as happy as a lark—got the job!"'),
	(205, 17, 'as hard as nails', 'keras / tidak berperasaan', 'The teacher is as hard as nails.', 'Gurunya sangat keras dan tidak berperasaan.', 'A: "Did the coach let you skip practice?"\nB: "No way—he’s as hard as nails."'),
	(206, 17, 'as hungry as a bear', 'lapar sekali', 'After exercising, I’m as hungry as a bear.', 'Setelah olahraga, aku lapar sekali.', 'A: "You ordered two burgers?"\nB: "I’m as hungry as a bear after that workout."'),
	(207, 17, 'as plain as day', 'jelas sekali', 'It’s as plain as day that they need to spend less.', 'Jelas sekali mereka harus mengurangi pengeluaran.', 'A: "Do you think he’s lying?"\nB: "It’s as plain as day—look at his face."'),
	(208, 17, 'as regular as clockwork', 'sangat teratur', 'Dylan catches the same bus every day. He’s as regular as clockwork.', 'Dylan naik bus yang sama setiap hari. Sangat teratur.', 'A: "When does the mail come?"\nB: "Two o’clock, as regular as clockwork."'),
	(209, 17, 'as sick as a dog', 'sakit parah', 'After eating bad shrimp, I was as sick as a dog.', 'Setelah makan udang busuk, aku sakit parah.', 'A: "You missed the party?"\nB: "Yeah, I was as sick as a dog all weekend."'),
	(210, 17, 'as stubborn as a mule', 'keras kepala', 'Maisie won’t eat broccoli even if you pay her. She’s as stubborn as a mule.', 'Maisie tak mau makan brokoli walau dibayar. Dia sangat keras kepala.', 'A: "He still refuses the vaccine?"\nB: "Stubborn as a mule—won’t listen to anyone."'),
	(211, 17, 'as weak as a kitten', 'lemah sekali', 'After being ill, Penny was as weak as a kitten.', 'Setelah sakit, Penny lemah sekali.', 'A: "Can she walk already?"\nB: "Only a few steps—still as weak as a kitten."'),
	(212, 18, 'to dillydally', 'berlama-lama / menunda-nunda', 'Debbie always dillydallies when she’s getting ready.', 'Debbie selalu berlama-lama kalau bersiap-siap.', 'A: "We\'re late!"\nB: "Stop dillydallying and let\'s go!"'),
	(213, 18, 'fuddy-duddy', 'kolot / kuno', 'Harold is a real fuddy-duddy. He never tries anything new.', 'Harold benar-benar kolot. Dia tak pernah mencoba hal baru.', 'A: "He still uses a flip phone!"\nB: "What a fuddy-duddy."'),
	(214, 18, 'hotshot', 'jagoan / ahli (sombong)', 'Jane thinks she’s a hotshot driver, but she keeps getting tickets.', 'Jane mengira dia jagoan nyetir, tapi terus kena tilang.', 'A: "He brags about his coding skills."\nB: "Yeah, a real hotshot until the server crashes."'),
	(215, 18, 'humdrum', 'membosankan / monoton', 'We got tired of our humdrum routine.', 'Kami bosan dengan rutinitas yang membosankan.', 'A: "How\'s the new job?"\nB: "Better than the humdrum office work I had before."'),
	(216, 18, 'hush-hush', 'rahasia / tertutup', 'Her job is very hush-hush.', 'Pekerjaannya sangat rahasia.', 'A: "What did you hear about the merger?"\nB: "It\'s hush-hush, but I\'ll text you later."'),
	(217, 18, 'mumbo jumbo', 'omong kosong / bahasa teknis membingungkan', 'I couldn’t understand all that mumbo jumbo in the insurance policy.', 'Aku tak mengerti omong kosong dalam polis asuransi itu.', 'A: "Explain the contract."\nB: "Too much legal mumbo jumbo—ask my lawyer."'),
	(218, 18, 'pitter-patter', 'suara langkah kecil / hujan rintik', 'We’ll soon hear the pitter-patter of tiny feet.', 'Segera kami akan mendengar suara langkah kaki kecil.', 'A: "What\'s that noise on the roof?"\nB: "Just the pitter-patter of rain."'),
	(219, 18, 'tip-top', 'sangat baik / prima', 'Her car is in tip-top condition.', 'Mobilnya dalam kondisi prima.', 'A: "How was the hotel?"\nB: "Service was tip-top, five stars."'),
	(220, 18, 'topsy-turvy', 'berantakan / kacau', 'Everything in this room is so topsy-turvy.', 'Semua di ruangan ini sangat berantakan.', 'A: "My schedule is topsy-turvy this week."\nB: "Color-code your calendar to fix it."'),
	(221, 18, 'wishy-washy', 'plin-plan / tidak tegas', 'My boss is very wishy-washy. He doesn’t give clear orders.', 'Bosku sangat plin-plan. Dia tak memberi perintah jelas.', 'A: "Did he say yes or no?"\nB: "Neither—typical wishy-washy answer."'),
	(222, 18, 'a wheeler-dealer', 'pebisnis licik / oportunis', 'Marvin is a wheeler-dealer in real estate.', 'Marvin pebisnis licik di bidang properti.', 'A: "He flipped three houses in a month!"\nB: "Yeah, a real wheeler-dealer."'),
	(223, 19, 'above all', 'yang paling penting', 'Pack warm clothes, but above all, don’t forget your toothbrush.', 'Bawa pakaian hangat, tapi yang paling penting, jangan lupa sikat gigi.', 'A: "Any tips for the interview?"\nB: "Be confident, but above all, be honest."'),
	(224, 19, 'after all', 'ternyata / pada akhirnya', 'I didn’t have time to do both, so I missed the movie after all.', 'Ternyata aku tak punya waktu untuk keduanya, jadi aku gagal nonton film.', 'A: "You took the train?"\nB: "The bus was late, so I walked after all."'),
	(225, 19, 'before long', 'sebentar lagi', 'Don’t worry. He’ll be here before long.', 'Jangan khawatir. Dia akan tiba sebentar lagi.', 'A: "Where’s the pizza?"\nB: "Before long, it’ll be at your door."'),
	(226, 19, 'every other', 'selang-seling', 'We each get every other one.', 'Kami bergantian mengambil satu.', 'A: "How often do you water the plants?"\nB: "Every other day is enough."'),
	(227, 19, 'more or less', 'kurang lebih', 'I’ve more or less finished painting the kitchen.', 'Aku sudah kurang lebih selesai mengecat dapur.', 'A: "Is the report ready?"\nB: "More or less—just need to spell-check."'),
	(228, 19, 'never mind', 'tidak usah dipikirkan', 'Never mind about the broken vase.', 'Jangan dipikirkan soal vas yang pecah.', 'A: "Sorry I spilled the coffee."\nB: "Never mind, I’ll wipe it up."'),
	(229, 19, 'no wonder', 'pantas saja / tidak heran', 'No wonder the TV wasn’t working—it wasn’t plugged in.', 'Pantas saja TV tidak menyala—ternyata tidak dicolok.', 'A: "He failed the test."\nB: "No wonder—he didn’t study at all."'),
	(230, 19, 'the other day', 'beberapa hari yang lalu', 'Lee won the grand prize the other day.', 'Lee memenangkan hadiah utama beberapa hari yang lalu.', 'A: "Nice shoes—new?"\nB: "Got them on sale the other day."'),
	(231, 19, 'quite a few', 'cukup banyak', 'I’ve asked them quite a few times not to play football near my window.', 'Aku sudah minta cukup banyak kali agar tidak main bola dekat jendela.', 'A: "Do you know anyone here?"\nB: "Quite a few—college friends mostly."'),
	(232, 19, 'sooner or later', 'cepat atau lambat', 'Sooner or later he’ll get here.', 'Cepat atau lambat dia akan tiba juga.', 'A: "Will they approve the budget?"\nB: "Sooner or later, yes—just keep pushing."'),
	(233, 19, 'take it easy', 'santai saja', 'Take it easy. You don’t have to drive so fast.', 'Santai saja. Tidak usah nyetir terlalu cepat.', 'A: "I’m stressed about the exam."\nB: "Take it easy—one chapter at a time.'),
	(234, 20, 'all of a sudden', 'tiba-tiba', 'All of a sudden I remembered I left the water running.', 'Tiba-tiba aku ingat kran air masih mengalir.', 'A: "Why did you jump?"\nB: "All of a sudden a mouse ran across my foot."'),
	(235, 20, 'as a matter of fact', 'sebenarnya / kenyataannya', 'As a matter of fact, he has three motorcycles.', 'Sebenarnya, dia punya tiga sepeda motor.', 'A: "Do you like jazz?"\nB: "As a matter of fact, I play sax every weekend."'),
	(236, 20, 'even so', 'meskipun begitu', 'Lydia is good at math. Even so, she had trouble with the last problem.', 'Lydia pandai matematika. Meskipun begitu, dia kesulitan dengan soal terakhir.', 'A: "The road was empty."\nB: "Even so, speeding is risky."'),
	(237, 20, 'for a change', 'untuk variasi / ganti suasana', 'Let’s have snails for a change.', 'Ayo makan keong untuk variasi.', 'A: "Pizza again?"\nB: "Let\'s cook Thai food for a change."'),
	(238, 20, 'for a start', 'sebagai alasan pertama', 'For a start, my alarm clock didn’t ring.', 'Pertama, alarmku tidak berbunyi.', 'A: "Why are you late?"\nB: "For a start, the bus broke down."'),
	(239, 20, 'in fact', 'sebenarnya', 'Yes, I’ve lost weight. In fact, I’ve lost 22 pounds.', 'Ya, aku turun berat badan. Sebenarnya, 22 pon.', 'A: "You look taller."\nB: "In fact, these shoes add two inches."'),
	(240, 20, 'might as well', 'lebih baik / tidak ada salahnya', 'We might as well stay home.', 'Lebih baik kita tinggal saja di rumah.', 'A: "The movie’s sold out."\nB: "We might as well stream something at home."'),
	(241, 20, 'once in a while', 'kadang-kadang', 'We see each other only once in a while.', 'Kami bertemu hanya kadang-kadang.', 'A: "Do you eat fast food?"\nB: "Once in a while, when I’m really busy."'),
	(242, 20, 'on purpose', 'dengan sengaja', 'Don’t tell me it was an accident. You did it on purpose.', 'Jangan bilang itu kecelakaan. Kamu lakukan dengan sengaja.', 'A: "Sorry I bumped you."\nB: "That was on purpose—you spilled my coffee!"'),
	(243, 20, 'on the whole', 'secara keseluruhan', 'On the whole, I try to avoid eating eggs.', 'Secara keseluruhan, aku usahakan tidak makan telur.', 'A: "How was the conference?"\nB: "On the whole, very informative."'),
	(244, 20, 'to take turns', 'bergiliran', 'Let’s take turns washing the dishes.', 'Ayo kita bergiliran mencuci piring.', 'A: "Who drives the kids today?"\nB: "We take turns; it\'s your turn."');
